import React, { useState, useEffect } from 'react';
import { Plus, Edit3, Trash2, Save, X, Eye, EyeOff, Upload, Image as ImageIcon, Lock, User } from 'lucide-react';

const App = () => {
    const [isAdmin, setIsAdmin] = useState(false);
    const [password, setPassword] = useState('');
    const [showAdminPanel, setShowAdminPanel] = useState(false);
    const [categories, setCategories] = useState([
        { id: 1, name: 'JavaScript', color: 'from-blue-500 to-purple-600' },
        { id: 2, name: 'React', color: 'from-cyan-500 to-blue-600' },
        { id: 3, name: 'CSS', color: 'from-pink-500 to-rose-600' },
        { id: 4, name: 'Node.js', color: 'from-green-500 to-emerald-600' }
    ]);
    const [posts, setPosts] = useState([
        {
            id: 1,
            title: 'Understanding React Hooks',
            content: 'React Hooks have revolutionized how we write functional components. They allow us to use state and other React features without writing a class.',
            category: 'React',
            date: '2024-01-15',
            image: 'https://placehold.co/600x400/1a1a1a/ffffff?text=React+Hooks'
        },
        {
            id: 2,
            title: 'CSS Grid vs Flexbox',
            content: 'Both CSS Grid and Flexbox are powerful layout systems. Grid is two-dimensional while Flexbox is one-dimensional. Choose based on your layout needs.',
            category: 'CSS',
            date: '2024-01-12',
            image: 'https://placehold.co/600x400/1a1a1a/ffffff?text=CSS+Grid'
        },
        {
            id: 3,
            title: 'Async/Await Best Practices',
            content: 'Async/await makes asynchronous code more readable. Always handle errors with try/catch blocks and avoid the async/await pyramid of doom.',
            category: 'JavaScript',
            date: '2024-01-10',
            image: 'https://placehold.co/600x400/1a1a1a/ffffff?text=Async+Await'
        }
    ]);
    const [editingPost, setEditingPost] = useState(null);
    const [editingCategory, setEditingCategory] = useState(null);
    const [newCategory, setNewCategory] = useState('');
    const [showImageModal, setShowImageModal] = useState(false);
    const [selectedImage, setSelectedImage] = useState('');
    const [showLoginForm, setShowLoginForm] = useState(false);

    const ADMIN_PASSWORD = 'devblog123';

    const handleLogin = () => {
        if (password === ADMIN_PASSWORD) {
            setIsAdmin(true);
            setShowLoginForm(false);
            setPassword('');
        } else {
            alert('Invalid password');
        }
    };

    const handleLogout = () => {
        setIsAdmin(false);
    };

    const handleSavePost = (post) => {
        if (editingPost) {
            setPosts(posts.map(p => p.id === editingPost.id ? post : p));
        } else {
            setPosts([...posts, { ...post, id: Date.now(), date: new Date().toISOString().split('T')[0] }]);
        }
        setEditingPost(null);
    };

    const handleDeletePost = (id) => {
        setPosts(posts.filter(post => post.id !== id));
    };

    const handleSaveCategory = (category) => {
        if (editingCategory) {
            setCategories(categories.map(c => c.id === editingCategory.id ? category : c));
        } else {
            setCategories([...categories, { ...category, id: Date.now() }]);
        }
        setEditingCategory(null);
        setNewCategory('');
    };

    const handleDeleteCategory = (id) => {
        setCategories(categories.filter(cat => cat.id !== id));
    };

    const handleImageUpload = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onload = (event) => {
                setSelectedImage(event.target.result);
                setShowImageModal(true);
            };
            reader.readAsDataURL(file);
        }
    };

    const PostForm = ({ post, onSave, onCancel }) => {
        const [formData, setFormData] = useState(post || {
            title: '',
            content: '',
            category: categories[0]?.name || '',
            image: ''
        });

        const handleImageSelect = () => {
            if (selectedImage) {
                setFormData({ ...formData, image: selectedImage });
                setShowImageModal(false);
                setSelectedImage('');
            }
        };

        return (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
                <div className="bg-gray-900 rounded-lg p-6 w-full max-w-2xl max-h-screen overflow-y-auto">
                    <h2 className="text-2xl font-bold text-white mb-4">
                        {post ? 'Edit Post' : 'Create New Post'}
                    </h2>
                    
                    <div className="space-y-4">
                        <div>
                            <label className="block text-gray-300 mb-2">Title</label>
                            <input
                                type="text"
                                value={formData.title}
                                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                                className="w-full bg-gray-800 text-white rounded px-3 py-2 border border-gray-700 focus:border-blue-500 focus:outline-none"
                                placeholder="Enter post title"
                            />
                        </div>
                        
                        <div>
                            <label className="block text-gray-300 mb-2">Category</label>
                            <select
                                value={formData.category}
                                onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                                className="w-full bg-gray-800 text-white rounded px-3 py-2 border border-gray-700 focus:border-blue-500 focus:outline-none"
                            >
                                {categories.map(cat => (
                                    <option key={cat.id} value={cat.name}>{cat.name}</option>
                                ))}
                            </select>
                        </div>
                        
                        <div>
                            <label className="block text-gray-300 mb-2">Content</label>
                            <textarea
                                value={formData.content}
                                onChange={(e) => setFormData({ ...formData, content: e.target.value })}
                                rows={6}
                                className="w-full bg-gray-800 text-white rounded px-3 py-2 border border-gray-700 focus:border-blue-500 focus:outline-none"
                                placeholder="Enter post content"
                            />
                        </div>
                        
                        <div>
                            <label className="block text-gray-300 mb-2">Featured Image</label>
                            <div className="flex items-center space-x-4">
                                {formData.image && (
                                    <img 
                                        src={formData.image} 
                                        alt="Preview" 
                                        className="w-20 h-20 object-cover rounded"
                                    />
                                )}
                                <button
                                    onClick={() => document.getElementById('image-upload').click()}
                                    className="bg-gradient-to-r from-purple-500 to-pink-600 text-white px-4 py-2 rounded-lg hover:from-purple-600 hover:to-pink-700 transition-all duration-300 flex items-center space-x-2"
                                >
                                    <Upload size={16} />
                                    <span>Upload Image</span>
                                </button>
                                <input
                                    id="image-upload"
                                    type="file"
                                    accept="image/*"
                                    onChange={handleImageUpload}
                                    className="hidden"
                                />
                            </div>
                        </div>
                    </div>
                    
                    <div className="flex justify-end space-x-3 mt-6">
                        <button
                            onClick={onCancel}
                            className="bg-gray-700 text-white px-4 py-2 rounded-lg hover:bg-gray-600 transition-colors duration-200 flex items-center space-x-2"
                        >
                            <X size={16} />
                            <span>Cancel</span>
                        </button>
                        <button
                            onClick={() => onSave(formData)}
                            className="bg-gradient-to-r from-green-500 to-emerald-600 text-white px-4 py-2 rounded-lg hover:from-green-600 hover:to-emerald-700 transition-all duration-300 flex items-center space-x-2"
                        >
                            <Save size={16} />
                            <span>Save Post</span>
                        </button>
                    </div>
                </div>
            </div>
        );
    };

    const CategoryForm = ({ category, onSave, onCancel }) => {
        const [formData, setFormData] = useState(category || { name: newCategory, color: 'from-blue-500 to-purple-600' });
        const [showColorPicker, setShowColorPicker] = useState(false);

        const colorOptions = [
            'from-blue-500 to-purple-600',
            'from-cyan-500 to-blue-600',
            'from-pink-500 to-rose-600',
            'from-green-500 to-emerald-600',
            'from-yellow-500 to-orange-600',
            'from-red-500 to-pink-600'
        ];

        return (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
                <div className="bg-gray-900 rounded-lg p-6 w-full max-w-md">
                    <h2 className="text-2xl font-bold text-white mb-4">
                        {category ? 'Edit Category' : 'Create New Category'}
                    </h2>
                    
                    <div className="space-y-4">
                        <div>
                            <label className="block text-gray-300 mb-2">Category Name</label>
                            <input
                                type="text"
                                value={formData.name}
                                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                                className="w-full bg-gray-800 text-white rounded px-3 py-2 border border-gray-700 focus:border-blue-500 focus:outline-none"
                                placeholder="Enter category name"
                            />
                        </div>
                        
                        <div>
                            <label className="block text-gray-300 mb-2">Color Gradient</label>
                            <div className="flex flex-wrap gap-2">
                                {colorOptions.map(color => (
                                    <button
                                        key={color}
                                        onClick={() => setFormData({ ...formData, color })}
                                        className={`w-8 h-8 rounded-full bg-gradient-to-r ${color} ${formData.color === color ? 'ring-2 ring-white' : ''}`}
                                    />
                                ))}
                            </div>
                        </div>
                    </div>
                    
                    <div className="flex justify-end space-x-3 mt-6">
                        <button
                            onClick={onCancel}
                            className="bg-gray-700 text-white px-4 py-2 rounded-lg hover:bg-gray-600 transition-colors duration-200 flex items-center space-x-2"
                        >
                            <X size={16} />
                            <span>Cancel</span>
                        </button>
                        <button
                            onClick={() => onSave(formData)}
                            className="bg-gradient-to-r from-green-500 to-emerald-600 text-white px-4 py-2 rounded-lg hover:from-green-600 hover:to-emerald-700 transition-all duration-300 flex items-center space-x-2"
                        >
                            <Save size={16} />
                            <span>Save Category</span>
                        </button>
                    </div>
                </div>
            </div>
        );
    };

    return (
        <div className="min-h-screen bg-gray-900 text-white">
            {/* Header */}
            <header className="bg-gray-800 border-b border-gray-700">
                <div className="container mx-auto px-4 py-4 flex justify-between items-center">
                    <h1 className="text-2xl font-bold">Dev Blog</h1>
                    <div className="flex items-center space-x-4">
                        {isAdmin ? (
                            <button
                                onClick={handleLogout}
                                className="bg-gradient-to-r from-red-500 to-pink-600 text-white px-4 py-2 rounded-lg hover:from-red-600 hover:to-pink-700 transition-all duration-300 flex items-center space-x-2"
                            >
                                <User size={16} />
                                <span>Logout</span>
                            </button>
                        ) : (
                            <button
                                onClick={() => setShowLoginForm(true)}
                                className="bg-gradient-to-r from-blue-500 to-purple-600 text-white px-4 py-2 rounded-lg hover:from-blue-600 hover:to-purple-700 transition-all duration-300 flex items-center space-x-2"
                            >
                                <Lock size={16} />
                                <span>Admin Login</span>
                            </button>
                        )}
                    </div>
                </div>
            </header>

            <div className="container mx-auto px-4 py-8">
                {!showAdminPanel ? (
                    /* Blog View - Visible to everyone */
                    <div>
                        <div className="text-center mb-8">
                            <h2 className="text-3xl font-bold mb-2">Dev Blog</h2>
                            <p className="text-gray-400">Sharing knowledge and insights for developers</p>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                            {posts.map((post) => (
                                <div
                                    key={post.id}
                                    className="bg-gray-800 rounded-lg overflow-hidden border border-gray-700 hover:border-gray-600 transition-all duration-300 hover:shadow-xl hover:-translate-y-1 cursor-pointer group"
                                >
                                    <div className="relative">
                                        <img 
                                            src={post.image} 
                                            alt={post.title}
                                            className="w-full h-48 object-cover"
                                        />
                                        <div className="absolute top-4 right-4 bg-black bg-opacity-50 px-2 py-1 rounded text-sm">
                                            {post.category}
                                        </div>
                                    </div>
                                    <div className="p-6">
                                        <h3 className="text-xl font-bold mb-2 group-hover:text-blue-400 transition-colors duration-200">
                                            {post.title}
                                        </h3>
                                        <p className="text-gray-300 mb-4 line-clamp-3">
                                            {post.content}
                                        </p>
                                        <div className="text-sm text-gray-400">
                                            {post.date}
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                ) : (
                    /* Admin Panel - Only visible to admin */
                    <div>
                        <div className="flex justify-between items-center mb-8">
                            <h2 className="text-3xl font-bold">Admin Dashboard</h2>
                            <button
                                onClick={() => setShowAdminPanel(false)}
                                className="bg-gradient-to-r from-gray-600 to-gray-700 text-white px-6 py-3 rounded-lg hover:from-gray-700 hover:to-gray-800 transition-all duration-300 flex items-center space-x-2"
                            >
                                <Eye size={20} />
                                <span>View Blog</span>
                            </button>
                        </div>

                        {/* Categories Section */}
                        <div className="mb-12">
                            <div className="flex justify-between items-center mb-6">
                                <h3 className="text-2xl font-bold">Categories</h3>
                                <button
                                    onClick={() => setEditingCategory({ name: '', color: 'from-blue-500 to-purple-600' })}
                                    className="bg-gradient-to-r from-green-500 to-emerald-600 text-white px-4 py-2 rounded-lg hover:from-green-600 hover:to-emerald-700 transition-all duration-300 flex items-center space-x-2"
                                >
                                    <Plus size={16} />
                                    <span>Add Category</span>
                                </button>
                            </div>
                            
                            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                                {categories.map((category) => (
                                    <div
                                        key={category.id}
                                        className={`bg-gradient-to-r ${category.color} rounded-lg p-4 text-white relative group hover:scale-105 transition-transform duration-300`}
                                    >
                                        <div className="flex justify-between items-center">
                                            <span className="font-semibold">{category.name}</span>
                                            <div className="opacity-0 group-hover:opacity-100 transition-opacity duration-200 flex space-x-2">
                                                <button
                                                    onClick={() => setEditingCategory(category)}
                                                    className="bg-white bg-opacity-20 p-1 rounded hover:bg-opacity-30"
                                                >
                                                    <Edit3 size={14} />
                                                </button>
                                                <button
                                                    onClick={() => handleDeleteCategory(category.id)}
                                                    className="bg-white bg-opacity-20 p-1 rounded hover:bg-opacity-30"
                                                >
                                                    <Trash2 size={14} />
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>

                        {/* Posts Section */}
                        <div>
                            <div className="flex justify-between items-center mb-6">
                                <h3 className="text-2xl font-bold">Blog Posts</h3>
                                <button
                                    onClick={() => setEditingPost(null)}
                                    className="bg-gradient-to-r from-blue-500 to-purple-600 text-white px-4 py-2 rounded-lg hover:from-blue-600 hover:to-purple-700 transition-all duration-300 flex items-center space-x-2"
                                >
                                    <Plus size={16} />
                                    <span>Add Post</span>
                                </button>
                            </div>
                            
                            <div className="space-y-4">
                                {posts.map((post) => (
                                    <div
                                        key={post.id}
                                        className="bg-gray-800 rounded-lg p-6 border border-gray-700 hover:border-gray-600 transition-all duration-300 hover:shadow-lg hover:-translate-y-1 group"
                                    >
                                        <div className="flex justify-between items-start mb-4">
                                            <div>
                                                <h4 className="text-xl font-bold mb-2">{post.title}</h4>
                                                <div className="flex items-center space-x-4 text-sm text-gray-400">
                                                    <span>{post.category}</span>
                                                    <span>{post.date}</span>
                                                </div>
                                            </div>
                                            <div className="opacity-0 group-hover:opacity-100 transition-opacity duration-200 flex space-x-2">
                                                <button
                                                    onClick={() => setEditingPost(post)}
                                                    className="bg-gradient-to-r from-blue-500 to-purple-600 text-white p-2 rounded-lg hover:from-blue-600 hover:to-purple-700 transition-all duration-300"
                                                >
                                                    <Edit3 size={16} />
                                                </button>
                                                <button
                                                    onClick={() => handleDeletePost(post.id)}
                                                    className="bg-gradient-to-r from-red-500 to-pink-600 text-white p-2 rounded-lg hover:from-red-600 hover:to-pink-700 transition-all duration-300"
                                                >
                                                    <Trash2 size={16} />
                                                </button>
                                            </div>
                                        </div>
                                        <p className="text-gray-300 mb-4">{post.content}</p>
                                        {post.image && (
                                            <img 
                                                src={post.image} 
                                                alt={post.title}
                                                className="w-full h-32 object-cover rounded mt-4"
                                            />
                                        )}
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                )}
            </div>

            {/* Login Modal */}
            {showLoginForm && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
                    <div className="bg-gray-800 rounded-lg p-6 w-full max-w-md">
                        <div className="flex justify-between items-center mb-4">
                            <h2 className="text-2xl font-bold">Admin Login</h2>
                            <button
                                onClick={() => setShowLoginForm(false)}
                                className="text-gray-400 hover:text-white"
                            >
                                <X size={24} />
                            </button>
                        </div>
                        <div className="space-y-4">
                            <div>
                                <label className="block text-gray-300 mb-2">Password</label>
                                <input
                                    type="password"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    placeholder="Enter admin password"
                                    className="w-full bg-gray-700 text-white rounded px-4 py-3 border border-gray-600 focus:border-blue-500 focus:outline-none"
                                    onKeyPress={(e) => e.key === 'Enter' && handleLogin()}
                                />
                            </div>
                            <button
                                onClick={handleLogin}
                                className="w-full bg-gradient-to-r from-blue-500 to-purple-600 text-white py-3 rounded-lg hover:from-blue-600 hover:to-purple-700 transition-all duration-300 font-semibold flex items-center justify-center space-x-2"
                            >
                                <Lock size={20} />
                                <span>Login</span>
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {/* Modals */}
            {editingPost !== null && (
                <PostForm
                    post={editingPost || null}
                    onSave={handleSavePost}
                    onCancel={() => setEditingPost(null)}
                />
            )}

            {editingCategory !== null && (
                <CategoryForm
                    category={editingCategory.name ? editingCategory : null}
                    onSave={handleSaveCategory}
                    onCancel={() => {
                        setEditingCategory(null);
                        if (!editingCategory.name) setNewCategory('');
                    }}
                />
            )}

            {showImageModal && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
                    <div className="bg-gray-900 rounded-lg p-6 max-w-md w-full">
                        <h3 className="text-xl font-bold mb-4">Select Image</h3>
                        <img 
                            src={selectedImage} 
                            alt="Selected" 
                            className="w-full h-64 object-cover rounded mb-4"
                        />
                        <div className="flex justify-end space-x-3">
                            <button
                                onClick={() => setShowImageModal(false)}
                                className="bg-gray-700 text-white px-4 py-2 rounded-lg hover:bg-gray-600 transition-colors duration-200"
                            >
                                Cancel
                            </button>
                            <button
                                onClick={handleImageSelect}
                                className="bg-gradient-to-r from-green-500 to-emerald-600 text-white px-4 py-2 rounded-lg hover:from-green-600 hover:to-emerald-700 transition-all duration-300"
                            >
                                Use This Image
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default App;
